mapper.insert:
INSERT INTO MEMBER (m_no, m_id, m_pw, m_email, m_birth, m_phone, m_name)
VALUES (
	MEMBER_M_NO_SEQ.NEXTVAL
	, #{m_id}
	, #{m_pw}
	, #{m_email}
	, #{m_birth}
	, #{m_phone}
	, #{m_name}
)

mapper.update:

UPDATE 
	MEMBER
SET 
	m_name = #{m_name} , m_pw = #{m_pw} , m_email = #{m_email} , m_phone = #{m_phone} 
WHERE 
	m_id = #{m_id}

   		UPDATE 
			MEMBER
		SET 
			m_pw = #{m_pw} 
		WHERE 
			m_id = #{m_id} AND m_email = #{m_email}
