mapper.insert:
INSERT INTO MEMBER (m_no, m_id, m_pw, m_email, m_birth, m_gender, m_phone, m_name)
VALUES (
	MEMBER_MNO_SEQ.NEXTVAL
	, #{m_id}
	, #{m_pw}
	, #{m_email}
	, #{m_birth}
	, #{m_gender}
	, #{m_phone}
	, #{m_name}
)

mapper.update:

UPDATE 
	TBL_USER 
SET 
	m_name = #{m_name} , m_pw = #{m_pw} , m_email = #{m_email} , m_phone = #{m_phone} 
WHERE 
	m_id = #{m_id}
